#include <iostream>
using namespace std;
const int N = 1e4 * 3 + 10;
int p[N], d[N], s[N]; // s表示size，仅有祖先节点才有效
int t, a, b;
char m[2];

void init()
{
    for (int i = 0; i < N; i++)
    {
        p[i] = i;
        s[i] = 1;
    }
}

int find(int x)
{
    if (x != p[x])
    {
        int u = find(p[x]);
        d[x] += d[p[x]];
        p[x] = u;
    }
    return p[x];
}

int merge(int x, int y)
{
    int px = find(x), py = find(y);
    p[px] = py;
    d[px] = s[py]; // 应该设置为y所属集合的节点数
    s[py] += s[px];
}

int query(int x, int y)
{
    int px = find(x), py = find(y);
    if (px != py)
        return -1;
    return max(0, abs(d[x] - d[y]) - 1); // 有可能x = y
}
int main()
{
    init();
    scanf("%d", &t);
    for (int i = 0; i < t; i++)
    {
        scanf("%s%d%d", m, &a, &b);
        if (m[0] == 'M')
        {
            if (find(a) == find(b))
                continue;
            merge(a, b);
        }
        else
        {
            printf("%d\n", query(a, b));
        }
    }
}
