	}
}

func TestClose(t *testing.T) {
	ch := make(chan int, 10)
	defer func(){
		close(ch)
	}()
	// do something ... 
}

// 无缓冲 channel，读
func TestClose(t *testing.T) {
	ch := make(chan int)
	close(ch)
	fmt.Println(<-ch) // 0
}

// 无缓冲 channel，写
func TestClose(t *testing.T) {
	ch := make(chan int)
	close(ch)
	ch <- 0 // panic: send on closed channel
}

// 有缓冲 channel, 写
func TestClose(t *testing.T) {
	ch := make(chan int, 10)
	close(ch)
	ch <- 1 // panic: send on closed channel
}

// 有缓冲 channel, 无写入值， 读
func TestClose(t *testing.T) {
	ch := make(chan int, 10)
	close(ch)
	fmt.Println(<-ch) // 0
}

// 有缓冲 channel，有写入值，读
func TestClose(t *testing.T) {
	ch := make(chan int, 10)
	for i := 1; i <= 10; i++ {
		ch <- i
	}
	close(ch)
	for i := range ch {
		fmt.Println(i) // 输出 1 到 10
	}
	fmt.Println(<-ch) // 输出 0
}
