class Solution {
    public int peakIndexInMountainArray(int[] arr) {
        int n = arr.length;
        int i = 1, j = n - 2;
        while (i < j) {
            int m = i + j >> 1;
            if (arr[m + 1] < arr[m] && arr[m] > arr[m - 1]) {
                return m;
            } else {
                if (arr[m + 1] > arr[m]) i = m + 1;
                else j = m - 1;
            }
        }
        return i;
    }
}

class Solution {
    public int findPeakElement(int[] a) {
    	// 特殊判断
        if (a.length == 0)
            return -1;
        if (a.length == 1)
            return 0;

        // 判断是否在边界
        if (a[0] > a[1])
            return 0;
        else if (a[a.length - 1] > a[a.length - 1 - 1])
            return a.length - 1;

        // 不在边界就一定在中间
        for (int i = 1; i < a.length - 1; i++) {
            if (a[i] > a[i - 1] && a[i] > a[i + 1])
                return i;
        }

        return -1;
    }
}

class Solution {
    public int findPeakElement(int[] nums) {
        int n = nums.length, i = 0, j = n - 1, m = i + j >> 1;
        if (n == 1) return 0;
        while (i < j ) {
            m = i + j >> 1;
            if (m == 0 || m == n - 1) break; // 边界条件提前退出
            if (nums[m - 1] < nums[m] && nums[m] > nums[m + 1]) {
                return m;
            } else {
                if (nums[m - 1] > nums[m + 1]) j = m - 1;
                else i = m + 1;
            }
        }
        if (m == 0) return nums[m] > nums[m + 1] ? m : m + 1;
        else if (m == n - 1) return nums[m] > nums[m - 1] ? m : m - 1;
        return i; // 非边界条件，正常返回i
    }
}

