class Solution {
    public boolean searchMatrix(int[][] matrix, int target) {
        for (int i = 0; i < matrix.length; i++) {
            for (int j = 0; j < matrix[i].length; j++) {
                if (matrix[i][j] == target) 
                    return true;
            }
        }
        return false;
    }
}

class Solution {
    public boolean searchMatrix(int[][] matrix, int target) {
        int r = matrix.length, c = matrix[0].length, size = r * c, i = 0, j = size - 1;
        while (i < j) {
            int m = i + j >> 1;
            int x = m / c, y = m % c;
            if (matrix[x][y] == target) {
                return true;
            } else if (matrix[x][y] > target) {
                j = m - 1;
            } else {
                i = m + 1;
            }
        }
        int x = i / c, y = i % c; 
        return target == matrix[x][y];
    }
}

class Solution {
    public boolean searchMatrix(int[][] matrix, int target) {
        int r = matrix.length, c = matrix[0].length, size = r * c;
        // 1. 确认target处于哪一行
				int ind = 0;
        for (ind = 0; ind < r; ind ++ ) {
            if (matrix[ind][0] <= target && matrix[ind][c - 1] >= target) break;
        }
        if (ind == r) return false;

        int i = 0, j = c - 1;
        int[] nums = matrix[ind];
			  // 注意边界条件，我们在循环内部判断i == j的情况，外层直接返回false即可
        while (i <= j) {
            int m = i + j >> 1;
            if (target == nums[m]) return true;
            else {
                if (target > nums[m]) i = m + 1;
                else j = m - 1;
            }
        }
        return false;
    }
}

class Solution {
    public boolean searchMatrix(int[][] matrix, int target) {
        int ind = findIndex(matrix, target) - 1;
        if (ind < 0) return false;
        return helper(matrix[ind], target);
    }

    public int findIndex(int[][] matrix, int target){
        int i = 0, j = matrix.length;
        while (i < j) {
            int m = i + j >> 1;
            // 如果起始元素大于target，hi不动，让lo逼近hi
            if (matrix[m][0] > target) {
                j = m;
            } else {
                i = m + 1;
            }
        }
        return i;
    }

    public boolean helper(int A[], int target){
        int i = 0, j = A.length - 1;
        while (i <= j) {
            int m = i + j >> 1;
            if (A[m] == target)
                return true;
            else if (A[m] > target) 
                j = m - 1;
            else
                i = m + 1;
        }
        return false;
    }
}

class Solution {
    public boolean searchMatrix(int[][] matrix, int target) {
        int n = matrix.length, m = matrix[0].length;
        int x = 0, y = m - 1;
        while (x >= 0 && x < n && y >= 0 && y < m) {
            if (matrix[x][y] == target) {
                return true;
            } else if (matrix[x][y] > target) {
                y -= 1;
            } else {
                x += 1;
            }
        }
        return false;
    }
}
